  <script>
    (() => {
      const plainFormatter = new Intl.NumberFormat("no-NO");
      const defaultFollowers = {
        discord: { count: 2500, label: "medlemmer" },
        twitch: { count: 3200, label: "f√∏lgere" },
        youtube: { count: 0, label: "abonnenter" },
        tiktok: { count: 4200, label: "f√∏lgere" },
        instagram: { count: 0, label: "f√∏lgere" },
      };

      const mergeFollowers = (base, overrides) => {
        const merged = { ...base };
        if (!overrides || typeof overrides !== "object") {
          return merged;
        }
        Object.entries(overrides).forEach(([key, value]) => {
          if (!value || typeof value !== "object") {
            return;
          }
          const existing = merged[key] || {};
          const nextCount =
            typeof value.count === "number"
              ? value.count
              : typeof existing.count === "number"
              ? existing.count
              : 0;
          merged[key] = { ...existing, ...value, count: nextCount };
        });
        return merged;
      };

      const formatCompact = (value) => {
        if (value < 100000) {
          return plainFormatter.format(value);
        }
        if (value >= 1000000) {
          const millions = value / 1000000;
          const decimals = millions >= 10 ? 0 : 1;
          return `${Number(millions.toFixed(decimals))}m`;
        }
        if (value >= 1000) {
          const thousands = value / 1000;
          const decimals = thousands >= 10 ? 0 : 1;
          return `${Number(thousands.toFixed(decimals))}k`;
        }
        return String(value);
      };

      const formatValue = (value, format) => {
        switch (format) {
          case "compact":
            return formatCompact(value);
          case "plain":
            return plainFormatter.format(value);
          default:
            return String(value);
        }
      };

      let followersConfig =
        typeof window !== "undefined" && window.fjCommunityFollowers
          ? mergeFollowers(defaultFollowers, window.fjCommunityFollowers)
          : { ...defaultFollowers };

      const applyFollowerValues = () => {
        document.querySelectorAll("[data-followers]").forEach((element) => {
          const key = element.dataset.followers;
          if (!key) {
            return;
          }
          const config = followersConfig[key];
          if (!config) {
            return;
          }
          const format = element.dataset.format || "compact";
          const prefix = element.dataset.prefix ?? "";
          const suffix = element.dataset.suffix ?? "";
          element.textContent = `${prefix}${formatValue(config.count, format)}${suffix}`;
        });

        document.querySelectorAll("[data-followers-total]").forEach((element) => {
          const keys = element.dataset.followersTotal
            ? element.dataset.followersTotal
                .split(",")
                .map((value) => value.trim())
                .filter(Boolean)
            : Object.keys(followersConfig);

          const totalValue = keys.reduce(
            (accumulator, key) => accumulator + (followersConfig[key]?.count ?? 0),
            0
          );

          const roundStrategy = element.dataset.round || "none";
          let valueToFormat = totalValue;
          if (roundStrategy === "up-1000") {
            valueToFormat = Math.ceil(totalValue / 1000) * 1000;
          } else if (roundStrategy === "up-100") {
            valueToFormat = Math.ceil(totalValue / 100) * 100;
          } else if (roundStrategy === "down-100") {
            valueToFormat = Math.floor(totalValue / 100) * 100;
          }

          const minValue = element.dataset.min ? Number.parseInt(element.dataset.min, 10) : null;
          if (typeof minValue === "number" && !Number.isNaN(minValue)) {
            valueToFormat = Math.max(valueToFormat, minValue);
          }

          const format = element.dataset.format || "plain";
          const prefix = element.dataset.prefix ?? "";
          const suffix = element.dataset.suffix ?? "";
          element.textContent = `${prefix}${formatValue(valueToFormat, format)}${suffix}`;
        });
      };

      const updateFollowers = (overrides) => {
        followersConfig = mergeFollowers(followersConfig, overrides);
        applyFollowerValues();
      };

      if (typeof window !== "undefined") {
        window.updateFjCommunityFollowers = updateFollowers;
        window.fjCommunityFollowers = followersConfig;
      }

      if (document.readyState === "loading") {
        document.addEventListener("DOMContentLoaded", applyFollowerValues, { once: true });
      } else {
        applyFollowerValues();
      }
    })();
  </script>

  <script>
      const SKIN_STORAGE_KEY = "fjolsenbanden-skin";
      const brandLogos = document.querySelectorAll("[data-brand-logo]");
      const themeToggleButtons = document.querySelectorAll("[data-theme-toggle]");
      const navLinks = document.querySelectorAll("[data-nav-link]");

      const readStoredSkin = () => {
        try {
          return localStorage.getItem(SKIN_STORAGE_KEY);
        } catch (error) {
          return null;
        }
      };

      const storeSkin = (value) => {
        try {
          localStorage.setItem(SKIN_STORAGE_KEY, value);
        } catch (error) {
          // ignore storage issues
        }
      };

      const setColorScheme = (skin) => {
        const scheme = skin === "light" ? "light" : "dark";
        document.documentElement.style.colorScheme = scheme;
      };

      const applySkin = (nextSkin) => {
        const skin = nextSkin === "light" ? "light" : "dark";
        document.body.dataset.skin = skin;
        document.body.classList.remove("theme-light", "theme-dark");
        document.body.classList.add(`theme-${skin}`);
        setColorScheme(skin);

        brandLogos.forEach((logo) => {
          const light = logo.getAttribute("data-logo-light");
          const dark = logo.getAttribute("data-logo-dark");
          const target = skin === "light" ? light : dark;
          if (target && logo.getAttribute("src") !== target) {
            logo.setAttribute("src", target);
          }
        });

        themeToggleButtons.forEach((button) => {
          const isLight = skin === "light";
          button.setAttribute("aria-pressed", String(isLight));
          const label = button.querySelector("[data-theme-toggle-label]");
          if (label) {
            label.textContent = isLight ? "Bytt til nattmodus" : "Bytt til dagmodus";
          }
          const iconToShow = isLight ? "moon" : "sun";
          button.querySelectorAll("[data-theme-icon]").forEach((icon) => {
            const shouldShow = icon.dataset.themeIcon === iconToShow;
            icon.classList.toggle("hidden", !shouldShow);
          });
        });

        storeSkin(skin);
      };

      const storedSkin = readStoredSkin();
      const initialSkin = storedSkin === "light" || storedSkin === "dark" ? storedSkin : document.body.dataset.skin || "dark";
      applySkin(initialSkin);

      themeToggleButtons.forEach((button) => {
        button.addEventListener("click", () => {
          const currentSkin = document.body.dataset.skin === "light" ? "light" : "dark";
          applySkin(currentSkin === "light" ? "dark" : "light");
        });
      });

      const footerMenuToggle = document.getElementById("footer-menu-toggle");
      const footerToggleLabel = footerMenuToggle ? footerMenuToggle.querySelector("[data-footer-toggle-label]") : null;
      const stickyFooter = document.querySelector(".fj-footer");
      const pageSections = Array.from(document.querySelectorAll("[data-section]"));

      const updateFooterOffset = () => {
        const offset = stickyFooter ? stickyFooter.offsetHeight : 0;
        document.documentElement.style.setProperty("--fj-footer-offset", `${offset}px`);
      };

      updateFooterOffset();

      if (stickyFooter && "ResizeObserver" in window) {
        const footerObserver = new ResizeObserver(() => updateFooterOffset());
        footerObserver.observe(stickyFooter);
      }

      window.addEventListener("resize", updateFooterOffset);

      const getNextSection = () => {
        if (!pageSections.length) {
          return null;
        }
        const currentScroll = window.scrollY;
        const currentIndex = pageSections.findIndex((section) => {
          const rect = section.getBoundingClientRect();
          const top = rect.top + window.scrollY;
          const bottom = rect.bottom + window.scrollY;
          return currentScroll >= top && currentScroll < bottom;
        });

        if (currentIndex === -1) {
          return pageSections[0];
        }

        return pageSections[currentIndex + 1] || pageSections[0];
      };

      const scrollToNextSection = () => {
        const target = getNextSection();
        if (!target) {
          return;
        }
        target.scrollIntoView({ behavior: "smooth", block: "start" });
      };

      if (footerToggleLabel) {
        footerToggleLabel.textContent = "Neste";
      }

      if (footerMenuToggle) {
        footerMenuToggle.addEventListener("click", scrollToNextSection);
      }

      const toggle = document.getElementById("nav-toggle");
      const menu = document.getElementById("nav-menu");
      const backdrop = document.getElementById("nav-backdrop");
      const toggleIcons = toggle ? toggle.querySelectorAll("[data-nav-toggle-icon]") : [];
      const toggleLabel = toggle ? toggle.querySelector("[data-nav-toggle-label]") : null;

      const setToggleState = (isOpen) => {
        if (!toggle) {
          return;
        }
        toggle.setAttribute("aria-expanded", String(isOpen));
        toggle.setAttribute("aria-label", isOpen ? "Lukk meny" : "√Öpne meny");
        if (toggleLabel) {
          toggleLabel.textContent = isOpen ? "Lukk meny" : "√Öpne meny";
        }
        toggleIcons.forEach((icon) => {
          const shouldShow = icon.dataset.navToggleIcon === (isOpen ? "close" : "open");
          icon.classList.toggle("hidden", !shouldShow);
        });
      };

      const openMenu = () => {
        if (!menu) {
          return;
        }
        menu.classList.remove("hidden");
        menu.classList.add("block");
        if (backdrop) {
          backdrop.classList.remove("hidden");
          backdrop.classList.add("block");
        }
        setToggleState(true);
      };

      const closeMenu = () => {
        if (!menu) {
          return;
        }
        menu.classList.add("hidden");
        menu.classList.remove("block");
        if (backdrop) {
          backdrop.classList.add("hidden");
          backdrop.classList.remove("block");
        }
        setToggleState(false);
      };

      if (toggle && menu) {
        toggle.addEventListener("click", () => {
          const isOpen = toggle.getAttribute("aria-expanded") === "true";
          if (isOpen) {
            closeMenu();
          } else {
            openMenu();
          }
        });
      }

      if (backdrop) {
        backdrop.addEventListener("click", closeMenu);
      }

      if (menu) {
        menu.querySelectorAll("[data-nav-close]").forEach((element) => {
          element.addEventListener("click", closeMenu);
        });
      }

      window.addEventListener("resize", () => {
        if (window.innerWidth >= 768) {
          closeMenu();
        }
      });

      window.addEventListener("keydown", (event) => {
        if (event.key === "Escape") {
          closeMenu();
        }
      });

      const contactForm = document.getElementById("contact-form");
      if (contactForm) {
        contactForm.addEventListener("submit", (event) => {
          event.preventDefault();
          const formData = new FormData(contactForm);
          const name = (formData.get("name") || "").toString().trim();
          const email = (formData.get("email") || "").toString().trim();
          const message = (formData.get("message") || "").toString().trim();
          const subject = encodeURIComponent("Kontakt via fjolsenbanden.no");
          const body = encodeURIComponent(`${message}\n\nNavn: ${name || "Ukjent"}\nE-post: ${email || "Ikke oppgitt"}`);
          window.location.href = `mailto:fjolsenfn@gmail.com?subject=${subject}&body=${body}`;
          contactForm.reset();
        });
      }

      const registrationForms = {
        under18: "https://docs.google.com/forms/d/e/1FAIpQLScqgnf92CTsQ2TLg8viDBMllbUUGcDPW8uG48yXSYFVYGdRSw/viewform",
        over18: "https://docs.google.com/forms/d/e/1FAIpQLSc9pIGhLF4impGUQhxBMZUbfp7mQOaraYxEL7VrsTRkbD9EgA/viewform",
      };

      const vippsDefaults = {
        name: "FjOlsen Fan",
        dateOfBirth: "2005-05-17",
        phone: "+47 987 65 432",
        email: "fjolsenfan@example.com",
      };

      const registrationModal = document.getElementById("registration-modal");
      const registrationOverlay = document.getElementById("registration-overlay");
      const openRegistrationButtons = document.querySelectorAll("[data-open-registration]");
      const closeRegistrationButtons = document.querySelectorAll("[data-close-registration]");
      const registrationTier = document.getElementById("registration-tier");
      const vippsLoginButton = document.getElementById("vipps-login");
      const vippsForm = document.getElementById("vipps-form");
      const vippsNameInput = document.getElementById("vipps-name");
      const vippsDobInput = document.getElementById("vipps-dob");
      const vippsPhoneInput = document.getElementById("vipps-phone");
      const vippsEmailInput = document.getElementById("vipps-email");
      const vippsAgeLabel = document.getElementById("vipps-age-label");
      const guardianSection = document.getElementById("guardian-section");
      const guardianForm = document.getElementById("guardian-form");
      const guardianInput = document.getElementById("guardian-phone");
      const guardianSuccess = document.getElementById("guardian-success");
      const guardianNumber = document.getElementById("guardian-number");
      const checkoutSection = document.getElementById("checkout-section");
      const checkoutButton = document.getElementById("checkout-button");
      const checkoutSuccess = document.getElementById("checkout-success");
      const completionSection = document.getElementById("completion-section");
      const completionLink = document.getElementById("completion-link");
      const vippsPhonePlaceholder = document.getElementById("vipps-phone-placeholder");

      const setHidden = (element, hidden) => {
        if (!element) return;
        element.classList.toggle("hidden", hidden);
      };

      const calculateAge = (value) => {
        if (!value) return 0;
        const dob = new Date(value);
        if (Number.isNaN(dob.getTime())) return 0;
        const today = new Date();
        let age = today.getFullYear() - dob.getFullYear();
        const monthDifference = today.getMonth() - dob.getMonth();
        if (monthDifference < 0 || (monthDifference === 0 && today.getDate() < dob.getDate())) {
          age -= 1;
        }
        return age;
      };

      const openRegistrationModal = (tierLabel) => {
        if (!registrationModal) return;
        registrationModal.classList.remove("hidden");
        registrationModal.classList.add("flex");
        document.body.classList.add("overflow-hidden");

        if (registrationTier) {
          registrationTier.textContent = tierLabel ? ` ‚Äì ${tierLabel}` : "";
        }

        if (vippsLoginButton) {
          vippsLoginButton.classList.remove("hidden");
        }
        if (vippsForm) {
          vippsForm.reset();
          vippsForm.classList.add("hidden");
        }
        if (vippsNameInput) vippsNameInput.value = vippsDefaults.name;
        if (vippsDobInput) vippsDobInput.value = vippsDefaults.dateOfBirth;
        if (vippsPhoneInput) vippsPhoneInput.value = vippsDefaults.phone;
        if (vippsEmailInput) vippsEmailInput.value = vippsDefaults.email;
        if (vippsAgeLabel) vippsAgeLabel.textContent = "Bekreft og g√• videre til neste steg.";

        if (guardianForm) guardianForm.reset();
        if (checkoutButton) checkoutButton.classList.remove("hidden");
        setHidden(guardianSection, true);
        setHidden(guardianSuccess, true);
        setHidden(checkoutSection, true);
        setHidden(checkoutSuccess, true);
        setHidden(completionSection, true);

        if (guardianNumber) guardianNumber.textContent = "";
        if (vippsPhonePlaceholder) vippsPhonePlaceholder.textContent = "";
        if (completionLink) completionLink.setAttribute("href", registrationForms.over18);

      };

      const closeRegistrationModal = () => {
        if (!registrationModal) return;
        registrationModal.classList.add("hidden");
        registrationModal.classList.remove("flex");
        document.body.classList.remove("overflow-hidden");
      };

      openRegistrationButtons.forEach((button) => {
        button.addEventListener("click", () => {
          const tierLabel = button.getAttribute("data-tier") || "";
          openRegistrationModal(tierLabel);
        });
      });

      closeRegistrationButtons.forEach((button) => {
        button.addEventListener("click", closeRegistrationModal);
      });

      if (registrationOverlay) {
        registrationOverlay.addEventListener("click", closeRegistrationModal);
      }

      document.addEventListener("keydown", (event) => {
        if (event.key === "Escape") {
          closeRegistrationModal();
        }
      });

      if (vippsLoginButton && vippsForm) {
        vippsLoginButton.addEventListener("click", () => {
          vippsLoginButton.classList.add("hidden");
          vippsForm.classList.remove("hidden");
        });
      }

      if (vippsForm) {
        vippsForm.addEventListener("submit", (event) => {
          event.preventDefault();
          const age = calculateAge(vippsDobInput ? vippsDobInput.value : "");
          if (vippsAgeLabel) {
            vippsAgeLabel.textContent = age ? `Alder ${age} √•r ‚Äì bekreft og g√• videre.` : "Bekreft og g√• videre til neste steg.";
          }
          if (vippsPhonePlaceholder && vippsPhoneInput) {
            vippsPhonePlaceholder.textContent = vippsPhoneInput.value;
          }

          setHidden(guardianSuccess, true);
          setHidden(checkoutSuccess, true);
          setHidden(completionSection, true);
          if (guardianForm) guardianForm.reset();
          if (checkoutButton) checkoutButton.classList.remove("hidden");

          if (age > 0 && age < 18) {
            setHidden(guardianSection, false);
            setHidden(checkoutSection, true);
            if (completionLink) completionLink.setAttribute("href", registrationForms.under18);
          } else {
            setHidden(guardianSection, true);
            setHidden(checkoutSection, false);
            if (completionLink) completionLink.setAttribute("href", registrationForms.over18);
          }
        });
      }

      if (guardianForm) {
        guardianForm.addEventListener("submit", (event) => {
          event.preventDefault();
          const number = guardianInput ? guardianInput.value.trim() : "";
          if (guardianNumber) {
            guardianNumber.textContent = number || "forelder";
          }
          setHidden(guardianSuccess, false);
          setHidden(completionSection, false);
          if (vippsPhonePlaceholder && vippsPhoneInput) {
            vippsPhonePlaceholder.textContent = vippsPhoneInput.value;
          }
        });
      }

      if (checkoutButton) {
        checkoutButton.addEventListener("click", () => {
          checkoutButton.classList.add("hidden");
          setHidden(checkoutSuccess, false);
          setHidden(completionSection, false);
          if (vippsPhonePlaceholder && vippsPhoneInput) {
            vippsPhonePlaceholder.textContent = vippsPhoneInput.value;
          }
        });
      }

      const previewOverlay = document.querySelector('[data-preview-overlay]');
      const previewFrame = document.querySelector('[data-preview-frame]');
      const previewTimer = document.querySelector('[data-preview-timer]');
      const previewUnmuteButton = document.querySelector('[data-video-unmute]');
      if (previewOverlay && previewFrame && previewTimer && previewUnmuteButton) {
        let remaining = Number.parseInt(previewTimer.textContent || '60', 10) || 60;
        const interval = window.setInterval(() => {
          remaining = Math.max(remaining - 1, 0);
          previewTimer.textContent = String(remaining);
          if (remaining === 0) {
            window.clearInterval(interval);
          }
        }, 1000);

        const enableFullStream = () => {
          previewOverlay.remove();
          window.clearInterval(interval);
          try {
            const url = new URL(previewFrame.src);
            url.searchParams.set('muted', 'false');
            previewFrame.src = url.toString();
          } catch (error) {
            previewFrame.src = previewFrame.src.includes('muted=true')
              ? previewFrame.src.replace('muted=true', 'muted=false')
              : `${previewFrame.src}${previewFrame.src.includes('?') ? '&' : '?'}muted=false`;
          }
        };

        previewUnmuteButton.addEventListener('click', (event) => {
          event.preventDefault();
          enableFullStream();
        });
      }

    </script>
    <script>
      (() => {
        const dismissLoader = () => {
          const loader = document.getElementById("loader");
          if (!loader || loader.dataset.dismissed === "true") {
            return;
          }
          loader.dataset.dismissed = "true";
          loader.style.transition = "opacity .4s ease";
          loader.style.opacity = "0";
          window.setTimeout(() => loader.remove(), 450);
        };

        if (document.readyState === "complete" || document.readyState === "interactive") {
          window.setTimeout(dismissLoader, 0);
        } else {
          document.addEventListener("DOMContentLoaded", dismissLoader, { once: true });
        }

        window.addEventListener("load", dismissLoader, { once: true });

        window.setTimeout(dismissLoader, 4000);
      })();
    </script>
  </body>
</html>
